mapper.select:
SELECT ec.id, ec.title, ec.price, ec.status, ec.gmt_create as gmtCreate, ec.lesson_num AS lessonNum, ec.cover, et. NAME AS teacherName, es1.title AS subjectLevelOne, es2.title AS subjectLevelTwo FROM edu_course ec, edu_course_description ecd, edu_teacher et, edu_subject es1, edu_subject es2
ec.id = ecd.id AND ec.teacher_id = et.id AND ec.subject_parent_id = es1.id AND ec.subject_id = es2.id
AND ec.title like concat('%',#{title},'%')
AND ec.status=#{status}
limit #{pageNumber},#{pageSize};

select count(*) from ( SELECT ec.id, ec.title, ec.price, ec.status, ec.gmt_create as gmtCreate, ec.lesson_num AS lessonNum, ec.cover, et. NAME AS teacherName, es1.title AS subjectLevelOne, es2.title AS subjectLevelTwo FROM edu_course ec, edu_course_description ecd, edu_teacher et, edu_subject es1, edu_subject es2
ec.id = ecd.id AND ec.teacher_id = et.id AND ec.subject_parent_id = es1.id AND ec.subject_id = es2.id
AND ec.title like concat('%',#{title},'%')
AND ec.status=#{status}
)s
